// src/pages/MyBorrowsPage.js
import React, { useState, useEffect, useCallback } from 'react';
// API functions: getMyBorrows, createBorrowSlip, submitBorrowSlip, deleteBorrowSlip (như cũ)
import { getMyBorrows, createBorrowSlip, submitBorrowSlip, deleteBorrowSlip } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

// Nhận thêm props từ App.js
function MyBorrowsPage({ borrowSelection, onRemoveBook, onClearSelection }) {
  const [slips, setSlips] = useState([]); // Danh sách phiếu mượn đã tạo
  const [loading, setLoading] = useState(true); // Loading danh sách phiếu
  const [error, setError] = useState(''); // Lỗi fetch/delete/submit phiếu
  const [actionLoading, setActionLoading] = useState(null); // ID slip/action đang loading
  const [createSlipLoading, setCreateSlipLoading] = useState(false); // Loading khi tạo phiếu từ lựa chọn
  const [createSlipError, setCreateSlipError] = useState(''); // Lỗi khi tạo phiếu từ lựa chọn
  const { logout } = useAuth();

  // Hàm fetch danh sách phiếu mượn (giữ nguyên, thêm useCallback)
  const fetchBorrows = useCallback(async () => {
    // Không reset loading ở đây vì có thể được gọi lại sau action
    // setLoading(true);
    setError(''); // Xóa lỗi cũ trước khi fetch
    try {
      const response = await getMyBorrows();
      setSlips(response.data || []); // Đảm bảo là mảng
    } catch (err) {
      console.error("Failed to fetch borrowing slips:", err);
       if (err.response && (err.response.status === 401 || err.response.status === 403)) {
         setError('Your session may have expired. Please log in again.');
         setTimeout(logout, 2000);
       } else {
           setError('Failed to load borrowing slips.');
       }
    } finally {
        // Chỉ set loading false nếu nó đang là true (chạy lần đầu)
      if(loading) setLoading(false);
    }
  }, [logout, loading]); // Thêm loading vào dependency

  // Fetch dữ liệu khi component mount
  useEffect(() => {
    setLoading(true); // Đặt loading true khi bắt đầu fetch
    fetchBorrows();
  }, [fetchBorrows]); // Chỉ phụ thuộc fetchBorrows (đã có logout và loading)


   // --- Hàm tạo phiếu mượn TỪ LỰA CHỌN ---
   const handleCreateSlipFromSelection = async () => {
        if (borrowSelection.length === 0) {
            alert("Your selection is empty. Please select some books first.");
            return;
        }
        setCreateSlipLoading(true);
        setCreateSlipError('');
        setError(''); // Xóa lỗi chung
        try {
            // Lấy danh sách book IDs từ selection
            const bookIds = borrowSelection.map(item => item.id);
            console.log("MyBorrowsPage: Creating slip with book IDs:", bookIds);
            // Gọi API tạo phiếu, gửi kèm bookIds
            // Backend cần hỗ trợ nhận { bookIds: [...] } trong body
            await createBorrowSlip({ bookIds });

            alert(`Borrowing slip created successfully with ${borrowSelection.length} book(s)! It's now in 'draft' status.`);
            onClearSelection(); // Gọi hàm xóa lựa chọn ở App.js
            setLoading(true); // Đặt loading để fetch lại danh sách slips
            fetchBorrows(); // Tải lại danh sách phiếu mượn
        } catch(err) {
             console.error("Failed to create slip from selection:", err);
             setCreateSlipError(err.response?.data?.message || 'Failed to create borrowing slip from selection.');
        } finally {
            setCreateSlipLoading(false);
        }
   };

  // --- Các hàm xử lý Submit/Delete phiếu mượn hiện có (giữ nguyên) ---
   const handleSubmitSlip = async (slipId) => { /* ... như cũ ... */
        setActionLoading(slipId); setError('');
        if (!window.confirm('Submit this borrowing slip?')) { setActionLoading(null); return; }
        try {
            await submitBorrowSlip(slipId); alert(`Slip ${slipId} submitted!`);
            setLoading(true); fetchBorrows(); // Reload list
        } catch (err) { setError(err.response?.data?.message || `Failed to submit slip ${slipId}.`); }
        finally { setActionLoading(null); }
    };
   const handleDeleteSlip = async (slipId) => { /* ... như cũ ... */
        setActionLoading(slipId); setError('');
        if (!window.confirm('Delete this draft slip?')) { setActionLoading(null); return; }
        try {
            await deleteBorrowSlip(slipId); alert(`Draft slip ${slipId} deleted!`);
            setLoading(true); fetchBorrows(); // Reload list
        } catch (err) { setError(err.response?.data?.message || `Failed to delete draft slip ${slipId}.`); }
        finally { setActionLoading(null); }
    };


  // --- UI Rendering ---
  return (
    <div className="container">
      <h2>My Borrowing Area</h2>

      {/* --- Phần Lựa chọn Sách Hiện tại --- */}
      <div style={{ border: '1px dashed #ccc', padding: '15px', marginBottom: '20px', borderRadius: '5px' }}>
          <h3>Your Book Selection</h3>
          {createSlipError && <p className="error-message">{createSlipError}</p>}
          {borrowSelection.length === 0 ? (
              <p>You haven't selected any books yet. Go to the <a href="/books">Books page</a> to select items.</p>
          ) : (
              <>
                  <ul style={{ maxHeight: '150px', overflowY: 'auto', marginBottom: '10px', borderBottom: '1px solid #eee', paddingBottom: '5px' }}>
                      {borrowSelection.map(item => (
                          <li key={item.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '5px 0', background: 'none' }}>
                              <span>{item.title} (ID: {item.id})</span>
                              <button
                                  onClick={() => onRemoveBook(item.id)}
                                  className="danger"
                                  style={{ padding: '2px 5px', fontSize: '0.8em' }}
                                  disabled={createSlipLoading}
                                >
                                  Remove
                              </button>
                          </li>
                      ))}
                  </ul>
                  <button onClick={handleCreateSlipFromSelection} disabled={createSlipLoading || borrowSelection.length === 0}>
                      {createSlipLoading ? 'Creating Slip...' : 'Create Draft Slip with Selection'}
                  </button>
                  <button onClick={onClearSelection} className="secondary" style={{ marginLeft: '10px' }} disabled={createSlipLoading || borrowSelection.length === 0}>
                      Clear Selection
                  </button>
              </>
          )}
      </div>
      {/* ------------------------------------ */}


      {/* --- Danh sách Phiếu Mượn Đã Tạo --- */}
      <h3>My Existing Borrowing Slips</h3>
       {error && <p className="error-message">{error}</p>} {/* Lỗi chung của trang */}
      {loading ? (
          <div className="loading">Loading your slips...</div>
      ) : slips.length === 0 ? (
        <p>You have no borrowing slips yet.</p>
      ) : (
        <ul>
          {slips.map((slip) => (
            <li key={slip.id}>
              <div className="slip-info">
                Slip ID: {slip.id} - Status: <strong>{slip.status}</strong>
                <br/>
                <small>Created: {new Date(slip.created_at).toLocaleString()} | Items: {slip.item_count}</small>
                {/* ... hiển thị các ngày khác nếu có ... */}
              </div>
              <div className="slip-actions">
                  {/* Nút cho phiếu 'draft' */}
                  {slip.status === 'draft' && (
                      <>
                         {/* TODO: Nút View/Edit Items (nếu làm phức tạp hơn) */}
                          <button
                              onClick={() => handleSubmitSlip(slip.id)}
                              disabled={actionLoading === slip.id || slip.item_count === 0}
                              title={slip.item_count === 0 ? "Cannot submit an empty slip" : ""}
                          >
                              {actionLoading === slip.id ? 'Submitting...' : 'Submit'}
                          </button>
                           <button
                              className="danger"
                              onClick={() => handleDeleteSlip(slip.id)}
                              disabled={actionLoading === slip.id}
                           >
                              {actionLoading === slip.id ? 'Deleting...' : 'Delete Draft'}
                           </button>
                      </>
                  )}
                   {/* TODO: Nút View Details cho các trạng thái khác */}
              </div>
            </li>
          ))}
        </ul>
      )}
       {/* CSS cho slip actions */}
       <style jsx>{`
            .slip-actions button { margin-left: 5px; }
       `}</style>
    </div>
  );
}

export default MyBorrowsPage;
