import React, { useState, useEffect } from 'react';
import { getBooks } from '../services/api';
import { useAuth } from '../contexts/AuthContext'; // Import useAuth

function BookListPage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { logout } = useAuth(); // Lấy hàm logout

  // Hàm fetch sách
  const fetchBooks = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await getBooks();
      setBooks(response.data);
    } catch (err) {
      console.error("Failed to fetch books:", err);
      if (err.response && (err.response.status === 401 || err.response.status === 403)) {
        setError('Your session may have expired. Please log in again.');
        // Tự động logout nếu token không hợp lệ/hết hạn
        setTimeout(logout, 2000); // Logout sau 2 giây để người dùng đọc thông báo
      } else {
          setError('Failed to load books. Please try refreshing the page.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Gọi fetchBooks khi component mount
  useEffect(() => {
    fetchBooks();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Chỉ chạy 1 lần

  // --- UI Rendering ---
  if (loading) return <div className="loading">Loading books...</div>;
  if (error) return <div className="container"><p className="error-message">{error}</p></div>;

  return (
    <div className="container">
      <h2>Available Books</h2>
      {/* TODO: Thêm nút "Add New Book" nếu user là Admin/Librarian */}
      {books.length === 0 ? (
        <p>No books available at the moment.</p>
      ) : (
        <ul>
          {books.map((book) => (
            <li key={book.id}>
               <div className="book-info">
                 <strong>{book.title}</strong> by {book.author || 'N/A'}
                 <br/>
                 <small>ISBN: {book.isbn || 'N/A'} | Qty: {book.quantity}</small>
              </div>
              {/* TODO: Thêm nút "Add to Draft" nếu user là người mượn và sách còn hàng */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default BookListPage;
