import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // Sử dụng hook đã tạo

function Navbar() {
  // Lấy các giá trị cần thiết từ AuthContext
  const { isAuthenticated, logout, userRole, isLoading } = useAuth();
  const navigate = useNavigate();

  // Hàm xử lý logout
  const handleLogout = () => {
    logout(); // Gọi hàm logout từ context
    navigate('/login', { replace: true }); // Điều hướng về trang login
  };

  // --- Logic hiển thị dựa trên vai trò ---
  // Đảm bảo so sánh chính xác tên vai trò (phân biệt hoa thường)
  // Lấy từ backend là 'Admin' và 'Thủ thư'
  const isAdmin = userRole === 'Admin';
  const isLibrarian = userRole === 'Thủ thư';
  const canManageBooks = isAdmin || isLibrarian; // Admin hoặc Thủ thư có thể quản lý sách

  // Không hiển thị gì nếu context đang load (tránh hiển thị sai)
  if (isLoading) {
      return (
          <nav>
              <ul>
                  <li>Loading...</li>
              </ul>
          </nav>
      );
  }

  // Debug log để kiểm tra giá trị
  // console.log('Navbar Render - isAuthenticated:', isAuthenticated, 'userRole:', userRole, 'isAdmin:', isAdmin, 'canManageBooks:', canManageBooks);

  return (
    <nav>
      <ul>
        {/* Link xem sách chung */}
        <li><Link to="/books">Books</Link></li>

        {/* === Phần cho người dùng đã đăng nhập === */}
        {isAuthenticated && (
          <>
            {/* Link quản lý sách (Admin & Thủ thư) */}
            {canManageBooks && (
              <li><Link to="/admin/books">Manage Books</Link></li>
            )}

            {/* Link quản lý người dùng (Chỉ Admin) */}
            {isAdmin && (
              <li><Link to="/admin/users">Manage Users</Link></li>
            )}

            {/* Link phiếu mượn cá nhân (Mọi người dùng đăng nhập) */}
            <li><Link to="/my-borrows">My Borrows</Link></li>

            {/* Nút Logout */}
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </>
        )}

        {/* === Phần cho người dùng chưa đăng nhập === */}
        {!isAuthenticated && (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
