// src/pages/BookListPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { getBooks } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

// Nhận thêm props từ App.js
function BookListPage({ borrowSelection, onSelectBook }) {
  const [allBooks, setAllBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { logout } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');

  const fetchBooks = async () => { /* ... như cũ ... */ };

  useEffect(() => {
    fetchBooks();
  }, [fetchBooks]); // <<< Thêm fetchBooks vào đây

  const handleSearchChange = (event) => { /* ... như cũ ... */ };

  const filteredBooks = useMemo(() => { /* ... như cũ ... */ }, [allBooks, searchQuery]);

  // --- Hàm kiểm tra sách đã được chọn chưa ---
  const isBookSelected = (bookId) => {
      return borrowSelection.some(item => item.id === bookId);
  };

  // --- UI Rendering ---
  if (loading) return <div className="loading">Loading books...</div>;
  if (error) return <div className="container"><p className="error-message">{error}</p></div>;

  return (
    <div className="container">
      <h2>Available Books</h2>
      {/* Thanh tìm kiếm */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by title or author..."
          value={searchQuery}
          onChange={handleSearchChange}
          style={{ padding: '8px', width: 'calc(100% - 20px)', border: '1px solid #ccc', borderRadius: '4px' }}
        />
      </div>

      {/* Danh sách sách đã lọc */}
      {filteredBooks.length === 0 ? (
         <p>No books found{searchQuery ? ` matching "${searchQuery}"` : ''}.</p>
      ) : (
        <ul>
          {filteredBooks.map((book) => (
            <li key={book.id}>
               <div className="book-info">
                 <strong>{book.title}</strong> by {book.author || 'N/A'}
                 <br/>
                 <small>ISBN: {book.isbn || 'N/A'} | Qty: {book.quantity} | Category: {book.category_name || 'N/A'}</small>
              </div>
              {/* --- Nút Chọn Sách --- */}
              <div className="book-actions">
                  <button
                      onClick={() => onSelectBook(book)} // Gọi hàm từ props khi nhấn
                      // Disable nếu sách đã được chọn HOẶC số lượng = 0
                      disabled={isBookSelected(book.id) || book.quantity <= 0}
                      title={book.quantity <= 0 ? "Out of stock" : (isBookSelected(book.id) ? "Already selected" : "Select this book for borrowing")}
                  >
                      {isBookSelected(book.id) ? 'Selected' : (book.quantity <= 0 ? 'Out of Stock' : 'Select Book')}
                  </button>
              </div>
              {/* -------------------- */}
            </li>
          ))}
        </ul>
      )}
       {/* Thêm style cho book-actions nếu cần */}
       <style jsx>{`
            .book-actions button {
                padding: 5px 10px;
                font-size: 0.9em;
                margin-left: 10px; /* Khoảng cách với book-info */
            }
       `}</style>
    </div>
  );
}

export default BookListPage;
