import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login'); // Chuyển hướng về trang login sau khi logout
  };

  return (
    <nav>
      <ul>
        <li><Link to={isAuthenticated ? "/books" : "/login"}>Books</Link></li>
        {isAuthenticated ? (
          <>
            <li><Link to="/my-borrows">My Borrows</Link></li>
            {/* Có thể thêm link Admin/Librarian ở đây nếu xác định được vai trò */}
            <li><button onClick={handleLogout}>Logout</button></li>
          </>
        ) : (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
