// src/components/Navbar.js

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // Import hook để lấy trạng thái auth

function Navbar() {
  // Lấy trạng thái xác thực và hàm logout từ AuthContext
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate(); // Hook để điều hướng sau khi logout

  // Hàm xử lý khi người dùng nhấn nút Logout
  const handleLogout = () => {
    logout(); // Gọi hàm logout từ context (xóa token, cập nhật state)
    navigate('/login', { replace: true }); // Chuyển hướng người dùng về trang đăng nhập
    console.log('User logged out and redirected to login.');
  };

  return (
    <nav>
      <ul>
        {/* Link "Books" luôn hiển thị, nhưng truy cập sẽ được bảo vệ bởi ProtectedRoute */}
        <li><Link to="/books">Books</Link></li>

        {/* Kiểm tra nếu người dùng đã đăng nhập */}
        {isAuthenticated ? (
          // Hiển thị các link cho người dùng đã đăng nhập
          <>
            <li><Link to="/my-borrows">My Borrows</Link></li>

            {/* Link quản lý người dùng (Cho demo, hiển thị cho mọi user đã đăng nhập) */}
            {/* Trong ứng dụng thực tế, nên kiểm tra vai trò/quyền ở đây nếu có */}
            <li><Link to="/admin/users">Manage Users</Link></li>

            {/* Nút Logout */}
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </>
        ) : (
          // Hiển thị các link cho người dùng chưa đăng nhập
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Register</Link></li>
          </>
        )}
      </ul>
      {/* Thêm CSS vào file index.css để tạo kiểu cho nav */}
    </nav>
  );
}

export default Navbar;
