import React, { useState, useEffect, useCallback } from 'react';
// Import API functions (KHÔNG cần getCategories ở đây nữa)
import { getBooks, createBook, updateBook, deleteBook } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
// Import BookForm component
import BookForm from '../components/BookForm'; // <<< Vẫn import BookForm

function BookManagementPage() {
    // State chỉ còn books
    const [books, setBooks] = useState([]);
    // const [categories, setCategories] = useState([]); // <<< XÓA DÒNG NÀY

    // State UI control (giữ nguyên)
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { logout } = useAuth();

    // State form (giữ nguyên)
    const [editingBook, setEditingBook] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [formLoading, setFormLoading] = useState(false);
    const [formError, setFormError] = useState('');

    // State delete (giữ nguyên)
    const [deleteLoading, setDeleteLoading] = useState(null);


    // --- Fetch data (CHỈ fetch books) ---
    const fetchData = useCallback(async () => {
        setLoading(true);
        setError('');
        try {
            // Chỉ cần gọi getBooks
            const booksResponse = await getBooks();
            setBooks(booksResponse.data);
        } catch (err) {
            console.error("Failed to fetch books:", err); // Sửa log lỗi
             if (err.response && (err.response.status === 401 || err.response.status === 403)) {
                setError('Access denied or session expired.'); // Sửa thông báo lỗi
                setTimeout(logout, 3000);
             } else {
                 setError('Failed to load book data.'); // Sửa thông báo lỗi
             }
        } finally {
            setLoading(false);
        }
    }, [logout]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    // --- Các hàm xử lý khác (handleShowAddForm, handleEditClick, handleCancelEdit, handleFormSubmit, handleDeleteBook) giữ nguyên ---
    // handleFormSubmit vẫn nhận payload đã có category_id từ BookForm

    const handleShowAddForm = () => { /* ... như cũ ... */ };
    const handleEditClick = (book) => { /* ... như cũ ... */ };
    const handleCancelEdit = () => { /* ... như cũ ... */ };
    const handleFormSubmit = async (payload) => { /* ... như cũ ... */ };
    const handleDeleteBook = async (bookId, bookTitle) => { /* ... như cũ ... */ };


    // --- UI Rendering ---
    if (loading) return <div className="loading">Loading Book Data...</div>;

    return (
        <div className="container">
            <h2>Book Management</h2>
            {error && <p className="error-message">{error}</p>}

            {!showForm && (
                 <button onClick={handleShowAddForm} style={{ marginBottom: '20px' }}>+ Add New Book</button>
            )}

            {/* Render BookForm và KHÔNG truyền prop 'categories' */}
            {showForm && (
                <BookForm
                    book={editingBook}
                    // categories={categories} // <<< XÓA PROP NÀY
                    onSubmit={handleFormSubmit}
                    onCancel={handleCancelEdit}
                    loading={formLoading}
                    error={formError}
                 />
            )}

            {/* --- Danh sách Sách (giữ nguyên) --- */}
            <h3>Existing Books</h3>
            {/* ... table hiển thị sách ... */}
            {/* ... style jsx ... */}
        </div>
    );
}

export default BookManagementPage;
