// src/App.js

import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';

// Import Context Provider và Hook
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Import Components
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

// Import Pages
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import BookListPage from './pages/BookListPage';
import MyBorrowsPage from './pages/MyBorrowsPage';
import AdminUserPage from './pages/AdminUserPage';

// Import CSS toàn cục
import './index.css';

/**
 * Component AppRoutes chứa logic định tuyến chính.
 * Nó sử dụng context xác thực để quyết định hiển thị gì.
 */
function AppRoutes() {
  // Lấy trạng thái xác thực và trạng thái loading từ context
  const { isAuthenticated, isLoading } = useAuth();

  // Quan trọng: Hiển thị trạng thái loading trong khi AuthContext đang khởi tạo
  // và kiểm tra token ban đầu. Điều này ngăn việc redirect sai lầm trước khi
  // trạng thái xác thực được xác định chính xác.
  if (isLoading) {
    return <div className="loading">Initializing Application...</div>;
  }

  return (
    <>
      <Navbar /> {/* Navbar hiển thị ở mọi trang */}
      <div className="app-content"> {/* Optional: một div bao bọc nội dung chính */}
        <Routes>
          {/* --- Public Routes --- */}
          {/* Chỉ truy cập được khi chưa đăng nhập, nếu đã đăng nhập thì redirect */}
          <Route
            path="/login"
            element={!isAuthenticated ? <LoginPage /> : <Navigate to="/books" replace />}
          />
          <Route
            path="/register"
            element={!isAuthenticated ? <RegisterPage /> : <Navigate to="/books" replace />}
          />

          {/* --- Protected Routes --- */}
          {/* Các route này yêu cầu người dùng phải đăng nhập */}
          <Route
            path="/books"
            element={
              <ProtectedRoute>
                <BookListPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/my-borrows"
            element={
              <ProtectedRoute>
                <MyBorrowsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/admin/users"
            element={
              <ProtectedRoute>
                {/* Trong ProtectedRoute có thể thêm kiểm tra quyền cụ thể nếu cần */}
                <AdminUserPage />
              </ProtectedRoute>
            }
          />

          {/* --- Default Route --- */}
          {/* Chuyển hướng từ trang gốc ('/') đến trang phù hợp dựa vào trạng thái đăng nhập */}
          <Route
            path="/"
            element={<Navigate to={isAuthenticated ? "/books" : "/login"} replace />}
          />

          {/* --- Fallback Route (404 Not Found) --- */}
          {/* Bất kỳ đường dẫn nào không khớp với các route trên sẽ hiển thị trang 404 */}
          <Route
            path="*"
            element={
              <div className="container">
                <h2>404 Not Found</h2>
                <p>Sorry, the page you are looking for does not exist.</p>
              </div>
            }
          />
        </Routes>
      </div>
    </>
  );
}

/**
 * Component App chính, đóng vai trò là gốc của ứng dụng.
 * Nhiệm vụ chính là thiết lập AuthProvider và BrowserRouter.
 */
function App() {
  return (
    // AuthProvider bao bọc toàn bộ ứng dụng để cung cấp context xác thực
    <AuthProvider>
      {/* BrowserRouter quản lý việc định tuyến trong ứng dụng */}
      <Router>
        {/* AppRoutes chứa toàn bộ cấu trúc và logic định tuyến */}
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
