import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext'; // Import AuthProvider và useAuth
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute'; // Import ProtectedRoute
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import BookListPage from './pages/BookListPage';
import MyBorrowsPage from './pages/MyBorrowsPage';
import './index.css';

// Component nội bộ để xử lý logic điều hướng dựa trên Auth state
function AppRoutes() {
  const { isAuthenticated, isLoading } = useAuth(); // Lấy trạng thái từ context

  // Quan trọng: Chờ AuthContext kiểm tra xong trước khi render routes
  if (isLoading) {
    return <div className="loading">Initializing Application...</div>;
  }

  return (
      <>
        <Navbar />
        <div /* Optional: Thêm class container ở đây nếu muốn layout chung */ >
            <Routes>
              {/* Public Routes */}
              <Route path="/login" element={!isAuthenticated ? <LoginPage /> : <Navigate to="/books" replace />} />
              <Route path="/register" element={!isAuthenticated ? <RegisterPage /> : <Navigate to="/books" replace />} />

              {/* Protected Routes */}
              <Route path="/books" element={
                <ProtectedRoute>
                  <BookListPage />
                </ProtectedRoute>
              } />
              <Route path="/my-borrows" element={
                <ProtectedRoute>
                  <MyBorrowsPage />
                </ProtectedRoute>
              } />
              {/* Thêm các route được bảo vệ khác */}

              {/* Redirect mặc định */}
              {/* Nếu đang loading, chưa thể quyết định, nếu không thì dựa vào isAuthenticated */}
              <Route path="/" element={<Navigate to={isAuthenticated ? "/books" : "/login"} replace />} />

              {/* Fallback route */}
              <Route path="*" element={<div className="container"><h2>404 Not Found</h2><p>The page you requested does not exist.</p></div>} />
            </Routes>
        </div>
      </>
  );
}


// Component App chính chỉ để cung cấp Context và Router
function App() {
  return (
    <AuthProvider> {/* Bọc toàn bộ ứng dụng trong AuthProvider */}
      <Router>
        <AppRoutes /> {/* Component chứa logic routes */}
      </Router>
    </AuthProvider>
  );
}

export default App;
