import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // Import hook để lấy trạng thái auth

function Navbar() {
  // Lấy trạng thái xác thực, vai trò và hàm logout từ AuthContext
  const { isAuthenticated, logout, userRole } = useAuth();
  const navigate = useNavigate(); // Hook để điều hướng

  // Hàm xử lý khi người dùng nhấn nút Logout
  const handleLogout = () => {
    logout(); // Gọi hàm logout từ context
    navigate('/login', { replace: true }); // Chuyển hướng về trang đăng nhập
  };

  // Biến kiểm tra quyền quản lý sách
  const canManageBooks = isAuthenticated && (userRole === 'Admin' || userRole === 'Thủ thư');
  // Biến kiểm tra quyền quản lý người dùng
  const canManageUsers = isAuthenticated && userRole === 'Admin';

  return (
    <nav>
      <ul>
        {/* Link "Books" luôn hiển thị */}
        <li><Link to="/books">Books</Link></li>

        {/* Các link chỉ hiển thị khi đã đăng nhập */}
        {isAuthenticated && (
          <>
            {/* Link quản lý sách (chỉ Admin hoặc Thủ thư) */}
            {canManageBooks && (
              <li><Link to="/admin/books">Manage Books</Link></li>
            )}

            {/* Link quản lý người dùng (chỉ Admin) */}
            {canManageUsers && (
              <li><Link to="/admin/users">Manage Users</Link></li>
            )}

             {/* Link phiếu mượn cá nhân (mọi người dùng đăng nhập) */}
             <li><Link to="/my-borrows">My Borrows</Link></li>


            {/* Nút Logout */}
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </>
        )}

        {/* Các link chỉ hiển thị khi chưa đăng nhập */}
        {!isAuthenticated && (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/register">Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
