import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';

// Import Context Provider và Hook
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Import Components
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute'; // Đảm bảo component này đã được tạo

// Import Pages
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import BookListPage from './pages/BookListPage';
import MyBorrowsPage from './pages/MyBorrowsPage';
import AdminUserPage from './pages/AdminUserPage';
import BookManagementPage from './pages/BookManagementPage'; // Import trang quản lý sách

// Import CSS toàn cục
import './index.css';

/**
 * Component AppRoutes chứa logic định tuyến chính.
 * Nó sử dụng context xác thực để quyết định hiển thị gì.
 */
function AppRoutes() {
  // Lấy trạng thái xác thực và trạng thái loading từ context
  const { isAuthenticated, isLoading, userRole } = useAuth(); // Lấy thêm userRole

  // Hiển thị trạng thái loading trong khi AuthContext đang khởi tạo
  if (isLoading) {
    return <div className="loading">Initializing Application...</div>;
  }

  return (
    <>
      {/* Navbar hiển thị ở mọi trang */}
      <Navbar />
      <div className="app-content"> {/* Optional: div bao bọc nội dung */}
        <Routes>
          {/* --- Public Routes --- */}
          <Route
            path="/login"
            element={!isAuthenticated ? <LoginPage /> : <Navigate to="/books" replace />}
          />
          <Route
            path="/register"
            element={!isAuthenticated ? <RegisterPage /> : <Navigate to="/books" replace />}
          />

          {/* --- Protected Routes --- */}
          {/* Trang sách chung (yêu cầu đăng nhập) */}
          <Route
            path="/books"
            element={
              <ProtectedRoute>
                <BookListPage />
              </ProtectedRoute>
            }
          />
          {/* Trang phiếu mượn cá nhân (yêu cầu đăng nhập) */}
          <Route
            path="/my-borrows"
            element={
              <ProtectedRoute>
                <MyBorrowsPage />
              </ProtectedRoute>
            }
          />
          {/* Trang quản lý người dùng (yêu cầu đăng nhập và vai trò Admin) */}
          <Route
            path="/admin/users"
            element={
              // Sử dụng prop requiredRole để kiểm tra ở frontend (tùy chọn nhưng tốt hơn)
              <ProtectedRoute requiredRole="Admin">
                <AdminUserPage />
              </ProtectedRoute>
            }
          />
          {/* Trang quản lý sách (yêu cầu đăng nhập và vai trò Admin hoặc Thủ thư) */}
          <Route
            path="/admin/books"
            element={
              // Có thể tạo logic kiểm tra nhiều vai trò trong ProtectedRoute
              // Hoặc đơn giản chỉ yêu cầu đăng nhập và dựa vào backend authorize
              <ProtectedRoute requiredRole={['Admin', 'Thủ thư']}> {/* Example: Pass array of roles */}
                <BookManagementPage />
              </ProtectedRoute>
              // Nếu ProtectedRoute chưa hỗ trợ mảng role, dùng cách đơn giản:
              // <ProtectedRoute>
              //   <BookManagementPage />
              // </ProtectedRoute>
            }
          />

          {/* --- Default Route --- */}
          <Route
            path="/"
            element={<Navigate to={isAuthenticated ? "/books" : "/login"} replace />}
          />

          {/* --- Fallback Route (404 Not Found) --- */}
          <Route
            path="*"
            element={
              <div className="container">
                <h2>404 Not Found</h2>
                <p>Sorry, the page you are looking for does not exist.</p>
              </div>
            }
          />
        </Routes>
      </div>
    </>
  );
}

/**
 * Component App chính, thiết lập AuthProvider và BrowserRouter.
 */
function App() {
  return (
    <AuthProvider> {/* Cung cấp context xác thực */}
      <Router> {/* Kích hoạt định tuyến */}
        <AppRoutes /> {/* Render cấu trúc định tuyến */}
      </Router>
    </AuthProvider>
  );
}

export default App;
